VALUES
(1, '1', 1, 'Näitä sääntöjä noudatetaan Suomen Kennelliitto - Finska Kennelklubben ry:n (jäljempänä Kennelliitto) hyväksymissä koiranäyttelyissä. Ilmoittaessaan koiran näyttelyyn näytteilleasettaja sitoutuu noudattamaan näitä sääntöjä sekä näitä sääntöjä täydentäviä Kennelliiton näyttelynjärjestämisohjeita ja FCI:n ohjeita sekä suostuu siihen, että hänen ilmoittautumislomakkeessa antamansa henkilö – ja osoitetiedot saa julkaista ja antaa ulkopuolisille, ellei hän ole tätä osoitetietojen osalta erikseen kieltänyt.', NULL, FALSE, FALSE),
(1, '1', 2, 'Näyttelystä on ilmoitettava vähintään kaksi kuukautta ennen sen toimeenpanemista Kennelliiton julkaisussa Koiramme-Våra Hundar.', NULL, FALSE, FALSE),
(1, '2', 1, 'Näyttelyiden tarkoituksena on tarjota koiraharrastajille mahdollisuus kilpailla omistamiensa/ esittämiensä koirien kanssa.  Koiranäyttelyissä koiraharrastajalla on tilaisuus vertailla jalostustyön tuloksia ja arvioida rodun jalostusmateriaalia.', NULL, FALSE, FALSE),
(1, '2', 2, 'Koirien hyvinvoinnin on oltava koiranäyttelyissä aina etusijalla.', NULL, FALSE, FALSE),
(1, '3', 1, 'Näyttelyyn saavat osallistua FCI:n tunnustaman maan kenneljärjestössä rekisteröidyt FCI:n ja Pohjoismaisen Kennelunionin PKU:n hyväksymien rotujen koirat, jotka on tunnistusmerkitty Kennelliiton hyväksymällä tavalla ja täyttävät voimassa olevat Kennelliiton hyväksymät terveysmääräykset.  Kansainvälisissä näyttelyissä rodut, joita FCI ei ole hyväksynyt, sijoitetaan näyttelyluettelossa omaan osioonsa ja ne eivät osallistu ryhmäkilpailuihin.', NULL, FALSE, FALSE),
(1, '3', 2, 'Arvostelun aikana ei koiralla saa olla kuonokoppaa tai muuta vastaavaa hallintavälinettä.', NULL, FALSE, FALSE),
(1, '3', 3, 'Näyttelyyn ei saa osallistua', NULL, FALSE, FALSE),
(1, '3', 4, 'koira, jota ei ole rokotettu Kennelliiton rokotusmääräysten mukaan', NULL, TRUE, TRUE),
(1, '3', 5, 'sairas koira', NULL, TRUE, TRUE),
(1, '3', 6, 'kantava narttu 30 vuorokautta ennen laskettua aikaa (laskettu aika = 63 vrk ensimmäisestä astutuksesta) ja synnyttänyt narttu alle 75 vrk synnytyksen jälkeen', NULL, TRUE, TRUE),
(1, '3', 7, 'Suomen lain vastaisesti typistetyt koirat (erillinen luettelo roduista, joilla esiintyy ns. luonnontöpöhäntäisyyttä)', NULL, TRUE, TRUE),
(1, '3', 8, 'vaarallisen vihainen koira ', NULL, TRUE, TRUE),
