(1, '3', 17,'kasvattajaryhmällä, jonka kasvattajalla tai yhdelläkin kasvattajisa on Kennelliiton kurinpitolautakunnan lisäseuraamuksena määräämä kielto tai Kennelliiton hallituksen määräämä tilapäinen kielto osallistua näyttelyihin näytteilleasettajana tai esittäjänä', NULL, TRUE, TRUE),
(1, '3.1', 1, 'Näyttelyssä noudatetaan kulloinkin voimassa olevaa Kennelliiton yleistä jääviyssääntöä.', NULL, FALSE, FALSE),
(1, '3.2', 1, 'Näyttelyn eläinlääkäri voi tarkastaa koiria ja todetessaan koiran sairaaksi, hänellä on oikeus poistaa se näyttelystä. Eläinlääkäri voi myös ottaa koirista näytteitä näyttelyn aikana tai viiden vuorokauden kuluessa näyttelyn päättymisestä.', NULL, FALSE, FALSE),
(1, '4', 1, 'Ilmoittautuminen on tehtävä joko postitse tai sähköisesti Kennelliiton hyväksymällä lomakkeella, joka on maksuineen toimitettava näyttelytoimikunnalle ennen ilmoittautumisajan päättymistä. Sekä maksupäivän että kirjeen postileiman päiväyksen on oltava viimeistään ilmoittautumisajan päättymispäivä.', NULL, FALSE, FALSE),
(1, '4', 2, 'Koira on ilmoitettava näyttelyyn sillä nimellä ja sen rotuisena, jolla se on rekisteröity. Se on ilmoitettava laatuarvosteluluokkaan, johon se on oikeutettu osallistumaan.', NULL, FALSE, FALSE),
(1, '4', 3, 'Ilmoittautumisajan päättymisen jälkeen koiraa ei voida siirtää toiseen luokkaan (ellei kysymyksessä ole järjestäjästä johtuva virhe).', NULL, FALSE, FALSE),
(1, '4', 4, 'Jos koirasta on ilmoitettaessa annettu vääriä tietoja, koira voidaan sulkea näyttelystä ja mitätöidä sille jo mahdollisesti annetut tulokset.', NULL, FALSE, FALSE),
(1, '5', 1, 'Ilmoittautumismaksu palautetaan näytteilleasettajan niin halutessa, jos', NULL, FALSE, FALSE),
(1, '5', 2, 'näyttely peruutetaan', NULL, TRUE, TRUE),
(1, '5', 3, 'ilmoittautuminen peruutetaan tuomarimuutoksen vuoksi, ellei näyttelytoimikunta ole etukäteen ennen ensimmäisen ilmoittautumisajan päättymistä ilmoittanut rodulle varatuomaria näyttelyn virallisessa tiedotteessa (Koiramme-Våra hundar)', NULL, TRUE, TRUE),
(1, '5', 4, 'näyttelyilmoittautuminen on myöhästynyt', NULL, TRUE, TRUE),
